  cols_align(align = "left", columns = outcome) |>
  cols_align(align = "center", columns = c(rmse, rsq_trad)) |>
  tab_spanner(label = "Evaluation Metrics", columns = c(rmse, rsq_trad)) |>
  tab_style(
